/** * @author Martin Krasser */class JaxrsController {
    def jaxrsContext     
    def handle = {        jaxrsContext.jaxrsService.process(new RequestWrapper(request), response)        // Flush the output stream on the unwrapped response to enforce        // response.committed set to true. This doesn't work with the        // original servlet response object (which violates the servlet        // spec). This is needed to prevent grails from special-handling        // html-related content types.        // See also http://jira.codehaus.org/browse/GRAILS-1223        unwrappedResponse.outputStream.flush()        return null    }
    private ServletResponse getUnwrappedResponse() {        def unwrapped = response        while (unwrapped instanceof ServletResponseWrapper) {            unwrapped = unwrapped.response        }        return unwrapped            } }
