    static mappings = {
        def logger = LogFactory.getLog(JaxrsUrlMappings.class)        def patternList = ConfigurationHolder.config.org.grails.jaxrs.url.mappings                if (!patternList) {            patternList = ['**']        } else if (patternList instanceof String) {            patternList = [patternList]        }              logger.info('URL mappings for JaxrsController:')        patternList.each { pattern ->            "/${pattern}"(controller:"jaxrs")
            "/${patterns}/**"(controller:"jaxrs")            logger.info("${pattern}")            logger.info("${pattern}/**")        }        	}
    
}
